        [Display(Name = "Date of Expiry")]
        public string EXPIRY_DATE_DISPLAY
        {
            get
            {
                string r = "";
                DateTime now = DateTime.Now;
                DateTime maxRetenton = new DateTime(1093881600000);
                if (this.EXPIRY_DATE != null)
                {
                    if (this.EXPIRY_DATE <= now && this.RETENTION_APPLICATION_DATE != null
                        && RETENTION_APPLICATION_DATE >= maxRetenton
                        && this.REMOVAL_DATE == null) { r += "@"; }
                    else
                    {
                        if (this.REMOVAL_DATE != null && this.RETENTION_APPLICATION_DATE != null
                            && (this.REMOVAL_DATE <= this.EXPIRY_DATE
                            || this.REMOVAL_DATE <= now)) { r += "-"; }
                        else
                        {
                            if (this.EXPIRY_DATE <= now) { r = this.EXPIRY_DATE + ""; }
                        }
                    }
                }
                return r;
            }
        }

        [Display(Name = "QP")]
        public string QP_DISPLAY
        {
            get
            {
                return "Y".Equals(this.WILLINGNESS_QP) ? "YES"
                  : "N".Equals(this.WILLINGNESS_QP) ? "NO"
                  : "No Indication";
            }
        }

